let callbackBtn=document.querySelector(".btn--callback");let callbackModal=document.querySelector(".callback.popup");let closeBtn=callbackModal.querySelector(".btn-close");let callbackName=callbackModal.querySelector("[name=callback-name]");let callbackMail=callbackModal.querySelector("[name=callback-email]");let callbackText=callbackModal.querySelector("[name=callback-text]");let callbackForm=callbackModal.querySelector(".callback__form");callbackBtn.addEventListener("click",function(evt){evt.preventDefault();callbackModal.classList.add("popup--show");callbackName.focus();});closeBtn.addEventListener("click",function(evt){evt.preventDefault();callbackModal.classList.remove("popup--show");callbackModal.classList.remove("popup--error");});callbackForm.addEventListener("submit",function(evt){if(!callbackMail.value||!callbackText.value||!callbackText.value){evt.preventDefault();callbackModal.classList.remove("popup--error");callbackModal.offsetWidth=callbackModal.offsetWidth;callbackModal.classList.add("popup--error");}});window.addEventListener("keydown",function(evt){if(evt.key==="Escape"){evt.preventDefault();if(callbackModal.classList.contains("popup--show")){callbackModal.classList.remove("popup--show");callbackModal.classList.remove("popup--error");}}});