let buyBtnClass="product-menu__buy";let cartAddModal=document.querySelector(".cart-add.popup");let shopBtn=cartAddModal.querySelector(".btn--white");closeBtn=cartAddModal.querySelector(".btn-close");document.addEventListener("click",function(evt){if(evt.target.classList.contains(buyBtnClass)){evt.preventDefault();cartAddModal.classList.add("popup--show");}});closeBtn.addEventListener("click",function(evt){evt.preventDefault();cartAddModal.classList.remove("popup--show");});shopBtn.addEventListener("click",function(evt){evt.preventDefault();cartAddModal.classList.remove("popup--show");});window.addEventListener("keydown",function(evt){if(evt.key==="Escape"){evt.preventDefault();if(cartAddModal.classList.contains("popup--show")){cartAddModal.classList.remove("popup--show");}}});