let mapLink=document.querySelector(".map-link");let mapModal=document.querySelector(".map.popup");closeBtn=mapModal.querySelector(".btn-close");mapLink.addEventListener("click",function(evt){evt.preventDefault();mapModal.classList.add("popup--show");});closeBtn.addEventListener("click",function(evt){evt.preventDefault();mapModal.classList.remove("popup--show");});window.addEventListener("keydown",function(evt){if(evt.key==="Escape"){evt.preventDefault();if(mapModal.classList.contains("popup--show")){mapModal.classList.remove("popup--show");}}});