let servicesContent=document.querySelector(".services__content");let servicesSlides=servicesContent.querySelector(".services__slides");let servicesCurrentSlide=servicesSlides.querySelector(".services__slide--active");let servicesToggles=servicesContent.querySelector(".services__control-list");let servicesCurrentToggle=servicesToggles.querySelector(".services__button--active");let servicesToggleNumber;for(let i=0;i<servicesToggles.children.length;i++){if(servicesToggles.children[i].querySelector("services__button--active")){servicesToggleNumber=i;}} servicesToggles.addEventListener("click",function(evt){if(evt.target.classList.contains("services__button")){evt.preventDefault();servicesCurrentToggle.classList.remove("services__button--active");servicesCurrentToggle=evt.target;servicesCurrentToggle.classList.add("services__button--active");for(let i=0;i<servicesToggles.children.length;i++){if(servicesToggles.children[i].querySelector(".services__button--active")){servicesToggleNumber=i;}}} if(!isNaN(servicesToggleNumber)){servicesCurrentSlide.classList.remove("services__slide--active");servicesCurrentSlide=servicesSlides.children[servicesToggleNumber];servicesCurrentSlide.classList.add("services__slide--active");}});